// Fail Close Types

CloseType.CONNECTION_FAILED = 200;
CloseType.CLOSED_BY_REMOTE = 201;
CloseType.NETWORK_ERROR = 202;
CloseType.CHANNEL_CLOSING = 203;

CloseType.VERSION_TIMEOUT = 210;
CloseType.VERACK_TIMEOUT = 211;
