CloseType.CONNECTION_LIMIT_PER_IP = 220;
CloseType.CONNECTION_LIMIT_DUMB = 221;

CloseType.FAILED_TO_PARSE_MESSAGE_TYPE = 230;
CloseType.UNEXPECTED_ACCOUNTS_TREE_CHUNK = 231;
